import { observer } from "mobx-react-lite";
import React, { useContext } from "react";
import ListGroup from 'react-bootstrap/ListGroup';
import { Context } from "..";
import { getAll } from "../http/userApi";

const About = observer((props) => {
    
    useEffect(() => {
        getAll().then(data => {
            user.setUser(data)
            console.log(data)
            user.setIsAuth(true)
        })
    }, [])

    const {user} = useContext(Context);
    const deleteUser = ()=>{
        console.log("delete")
    }
    return (
        <ListGroup>
            {user.users.map(l=>{return(
                <ListGroup.Item>
                    {l.email}
                    <p onClick={()=>deleteUser()}>Удалить</p>
                </ListGroup.Item>
                    );
                })
            }
        </ListGroup>
    );
})


export default About;
